src/app/globals.css.content:
:root {
  --background: #fafaff;
  --foreground: #30343f;
}

/* Reset y configuración base para responsividad */
* {
  box-sizing: border-box;
}

html, body {
  overflow-x: hidden; /* Evita scroll horizontal */
  width: 100%;
  margin: 0;
  padding: 0;
}

h1, h2, h3, h4, h5, h6 {
  font-family: var(--font-monserrat); 
}

body {
  background: var(--background);
  color: var(--foreground);
  font-family: var(--font-lato);
  padding-top: 70px; /* Espacio para el header fijo */
}

src/app/home.css.content:
.welcolme-section {
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #273469;
    width: 100%;
    height: 95vh;
    box-sizing: border-box;
    overflow-x: hidden;
}

.text-section {
    flex: 1;
    display: flex;
    flex-direction: column;
    gap: 0.9rem;
    margin: 0 5rem;
    box-sizing: border-box;
}

.text-section h1 {
    font-size: 4rem;
    font-weight: 800;
    line-height: 1.2;
    color: #fafaff;
    margin: 0;
}

.text-section p {
    font-size: 1.4rem;
    line-height: 1.6;
    max-width: 500px;
    color: #fafaff;
    margin: 0;
}

.explore-button {
    background-color: #fafaff;
    color: #2a2f4f;
    border: none;
    border-radius: 50px;
    padding: 14px 36px;
    font-size: 1.2rem;
    font-weight: 600;
    cursor: pointer;
    transition: transform 0.2s ease, background-color 0.2s ease;
    align-self: flex-start;
}

.explore-button:hover {
    transform: scale(1.05);
    background-color: #fafaff;
}

.image-section {
    flex: 1;
    position: relative;
    height: 550px;
    margin-right: 4rem;
    box-sizing: border-box;
}

.responsive-image {
    object-fit: contain;
}

.info-section{
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    height: 45vh;
    box-sizing: border-box;
    overflow-x: hidden;
}
.info-item{
    flex: 1;
    display: flex;
    flex-direction: column;
    gap: 0.9rem;
    margin: 0 5rem;
}
.info-item h2{
    font-size: 3rem;
    font-weight: 800;
    line-height: 1.2;
    color: #30343f;
    margin: 0;
}
.info-item p{
    font-size: 1.3rem;
    line-height: 1.5;
    max-width: 700px;
    color: #30343f;
    margin: 0;
} 

.content-section{
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
    height: 100vh;
    box-sizing: border-box;
    overflow-x: hidden;
}
.content-title h2{
    font-size: 2.9rem;
    font-weight: 800;
    color: #30343f;
}

.content-items {
    width: 100%;
    min-height: 60vh;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    gap: 2rem;
    padding: 2rem 0;
}
/* Contenedor de cada tarjeta completa (imagen + título) */
.content-card {
  width: 25%;
  max-width: 350px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
}

.content-item {
  width: 100%;
  aspect-ratio: 1 / 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-color: #e4d9ff;
  border-radius: 30px;
  padding: 1rem;
  position: relative;
  overflow: hidden;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.content-item:hover {
  transform: translateY(-5px);
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
}

/* Overlay de descripción */
.content-item-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: linear-gradient(to top, rgba(48, 52, 63, 0.95), rgba(48, 52, 63, 0.85));
  color: #fafaff;
  padding: 20px;
  border-radius: 0 0 30px 30px;
  transform: translateY(100%);
  opacity: 0;
  transition: all 0.4s ease;
  z-index: 3;
}

.content-item:hover .content-item-overlay {
  transform: translateY(0);
  opacity: 1;
}

.content-item-overlay p {
  margin: 0;
  font-size: 0.9rem;
  line-height: 1.4;
  text-align: center;
  font-family: var(--font-lato);
}

/* Estilos para la imagen */
.content-item-img {
  object-fit: cover; /* ✨ La propiedad clave: escala la imagen para cubrir sin distorsión */
  /* object-position: center;  puedes usar esto para centrar una parte específica de la imagen */
}

/* Título debajo de cada tarjeta */
.content-item-title {
  font-size: 1.5rem;
  font-weight: 700;
  color: #30343f;
  margin: 0;
  text-align: center;
  font-family: var(--font-monserrat);
}

/* Unificamos TODO lo del ícono en una sola regla */
.icon-conten {
  position: absolute;   /* ✨ Clave: Lo saca del flujo normal para posicionarlo en la esquina */
  z-index: 2;           /* ✨ Clave: Lo pone por encima de la imagen */
  top: 1.5rem;
  right: 1.5rem;
  background-color: #ffffff;
  border-radius: 50%;
  width: 50px;
  height: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background-color 0.2s ease-in-out;
}

.icon-conten:hover {
    background-color: #c8c6c6;
}
.contac-section{
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
    height: 40vh;
    background-color: #fafaff;
    box-sizing: border-box;
    overflow-x: hidden;
}
.contac-items {
    width: 100%;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    gap: 4rem;
}
.contac-title h2{
    font-size: 2.5rem;
    font-weight: 800;
    color: #30343f;
}
.contac-item {
    width: 25%;
    max-width: 110px;
    aspect-ratio: 1 / 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    position: relative;
    perspective: 800px;
}

.icon-logo-1, .icon-logo-2, .icon-logo-3, .icon-logo-4, .icon-logo-5, .icon-logo-6 {
    transition: transform 0.3s ease-out;
}

[class^="icon-logo-"] {
    transition: transform 0.3s ease-out, filter 0.3s ease-out;
    }
    .contac-item:hover [class^="icon-logo-"] {
    transform: translateY(-10px) scale(1.1);
    filter: drop-shadow(0 10px 4px rgba(0, 0, 0, 0.2));
    }

    .final-section {
        display: flex;
        align-items: center;
        justify-content: center;
        background-color: #fafaff;
        width: 100%;
        height: 90vh;
        box-sizing: border-box;
        overflow-x: hidden;
    }
    .text-sectionB {
        flex: 1;
        display: flex;
        flex-direction: column;
        gap: 0.9rem;
        margin: 0 5rem;
    }

    .text-sectionB h1 {
        font-size: 4rem;
        font-weight: 800;
        line-height: 1.2;
        color: #30343f;
        margin: 0;
    }

    .text-sectionB p {
        font-size: 1.4rem;
        line-height: 1.6;
        max-width: 500px;
        color: #30343f;
        margin: 0;
    }

    .explore-buttonB {
        background-color: #30343f;
        color: #e4d9ff;
        border: none;
        border-radius: 50px;
        padding: 14px 36px;
        font-size: 1.2rem;
        font-weight: 600;
        cursor: pointer;
        transition: transform 0.2s ease, background-color 0.2s ease;
        align-self: flex-start;
    }

    .explore-buttonB:hover {
        transform: scale(1.05);
        background-color: #30343f;
    }

    .image-sectionB {
        flex: 1;
        position: relative;
        height: 550px;
        margin-right: 4rem;
    }

    .responsive-imageB {
        object-fit: contain;
    }

/* --- ESTILOS RESPONSIVOS --- */

/* --- Pantallas Medianas (Tablets) - hasta 1024px --- */
@media (max-width: 1024px) {
    .text-section,
    .text-sectionB {
      margin: 0 3rem; /* Reduce el margen lateral */
    }

    .text-section h1,
    .text-sectionB h1 {
      font-size: 3.2rem; /* Reduce el tamaño de la fuente del título */
    }

    .text-section p,
    .text-sectionB p {
      font-size: 1.2rem; /* Reduce el tamaño de la fuente del párrafo */
    }

    .info-item h2 {
      font-size: 2.5rem;
    }
    .info-item p {
      font-size: 1.1rem;
    }

    .content-items {
      gap: 1.5rem; /* Reduce el espacio entre las tarjetas */
    }

    .content-card {
      width: 30%; /* Ajusta el ancho de las tarjetas para tablets */
      max-width: 250px; /* Tamaño máximo para tablets */
    }

    .content-item-title {
      font-size: 1.4rem; /* Tamaño apropiado para tablets */
    }

    .contac-items {
      gap: 2rem; /* Reduce el espacio entre los logos */
    }
  }


  /* --- Pantallas Pequeñas (Móviles) - hasta 768px --- */
  @media (max-width: 768px) {
    /* --- Sección de Bienvenida --- */
    .welcolme-section {
      flex-direction: column; /* Apila el texto sobre la imagen */
      height: auto; /* Altura automática para adaptarse al contenido */
      padding: 2rem 1rem; /* Padding reducido */
      text-align: center;
      margin: 0;
      overflow-x: hidden;
    }

    .text-section {
      margin: 0; /* Sin margen lateral */
      padding: 0; /* Sin padding extra */
      align-items: center; /* Centra los elementos */
      max-width: 100%;
    }

    .text-section h1 {
      font-size: 2.5rem; /* Tamaño de fuente más pequeño para móviles */
      margin: 0 0 1rem 0;
      padding: 0;
    }

    .text-section p {
      font-size: 1rem;
      margin: 0 0 1rem 0;
      padding: 0;
      max-width: 100%;
    }

    .image-section {
      order: -1; /* Mueve la imagen arriba del texto */
      height: 300px; /* Reduce la altura de la imagen */
      width: 90%;
      margin: 0 auto 2rem auto; /* Centra la imagen */
      max-width: 100%;
    }

    /* --- Separadores --- */
    .separation-1 {
      height: 150px;
    }

    .separation-2 {
      height: 100px;
    }

    /* --- Sección de Información --- */
    .info-section {
      height: auto;
      padding: 2rem 1rem; /* Padding reducido */
      text-align: center;
      margin: 0;
    }

    .info-item {
      margin: 0;
      padding: 0;
      max-width: 100%;
    }

    .info-item h2 {
      font-size: 2rem;
      margin: 0 0 1rem 0;
      padding: 0;
    }

    .info-item p {
      margin: 0;
      padding: 0;
      max-width: 100%;
    }

    /* --- Sección de Contenido --- */
    .content-section {
      height: auto;
      padding: 2rem 0;
      text-align: center; /* Centra el contenido */
    }

    .content-title {
      text-align: center; /* Centra el título */
    }

    .content-title h2 {
      text-align: center; /* Asegura que el título esté centrado */
    }

    .content-items {
      flex-direction: column; /* Apila las tarjetas verticalmente */
      align-items: center;
      gap: 2rem;
    }

    .content-card {
      width: 85%; /* Las tarjetas ocupan más ancho */
      max-width: 400px;
    }

    .content-item {
      width: 100%;
    }

    .content-item-title {
      font-size: 1.3rem; /* Título más pequeño en móvil */
    }

    /* --- Sección de Contacto --- */
    .contac-section {
      height: auto;
      padding: 2rem 1rem; /* Padding reducido */
      margin: 0;
    }

    .contac-items {
      flex-wrap: wrap; /* Permite que los logos se envuelvan en varias líneas */
      gap: 1.8rem; /* Espacio moderado entre las imágenes */
      justify-content: center; /* Centra las imágenes */
      padding: 0 1rem; /* Padding adicional a los lados */
    }

    .contac-item {
      width: auto; /* Ancho automático para los logos */
      max-width: 75px; /* Tamaño intermedio */
    }

    .contac-item img {
      width: 75px !important; /* Tamaño intermedio */
      height: 75px !important;
    }

    /* --- Sección Final --- */
    .final-section {
      flex-direction: column; /* Apila los elementos */
      height: auto;
      padding: 2rem 1rem; /* Padding reducido */
      text-align: center;
      margin: 0;
    }

    .text-sectionB {
      margin: 0;
      padding: 0;
      align-items: center; /* Centra los elementos */
      max-width: 100%;
    }

    .text-sectionB h1 {
      font-size: 2.5rem;
      margin: 0 0 1rem 0;
      padding: 0;
    }

    .text-sectionB p {
      font-size: 1rem;
      margin: 0 0 1rem 0;
      padding: 0;
      max-width: 100%;
    }

    .image-sectionB {
      height: 300px;
      width: 90%;
      margin: 2rem auto 0 auto; /* Centra la imagen */
      max-width: 100%;
    }
  }

src/app/layout.js.content:
import { Montserrat, Lato } from "next/font/google";
import Header from "./componets/principal/Header";
import Footer from "./componets/principal/Footer";
import "./globals.css";

const montserrat = Montserrat({
  variable: "--font-monserrat",
  subsets: ["latin"],
});

const lato = Lato({
  variable: "--font-lato",
  subsets: ["latin"],
  weight: ["100", "300", "400", "700", "900"],
});

export const metadata = {
  title: "Onovatech",
  description: "Generated by create next app",
};

export default function RootLayout({ children }) {
  return (
    <html lang="en">
      <body
        className={`${montserrat.variable} ${lato.variable} antialiased`}
      >
        <Header /> 
        {children}
        <Footer />
      </body>
    </html>
  );
}

