src/app/componets/principal/Footer.css.content:
.footer {
    background-color: #30343f;
    color: #e4d9ff;
    padding: 60px 40px 20px 40px;
    font-family: var(--font-lato);
    width: 100%;
    box-sizing: border-box;
    overflow-x: hidden;
}


.footer-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
    gap: 40px;
    margin-bottom: 40px;
}

.footer-column h3, .footer-column h4 {
    color: #fff;
    margin-bottom: 20px;
    font-family: var(--font-monserrat);
}

.footer-logo {
    font-size: 2rem;
    font-weight: 700;
}

.footer-about p {
    line-height: 1.6;
    margin-bottom: 20px;
}

.footer-links ul {
    list-style: none;
    padding: 0;
    margin: 0;
}

.footer-links ul li {
    margin-bottom: 12px;
}

.footer-links a {
    color: #ccc;
    text-decoration: none;
    transition: color 0.3s ease;
}

.footer-links a:hover {
    color: #fff;
    text-decoration: underline;
}


.social-icons {
    display: flex;
    gap: 15px; 
}

.social-icons a {
    color: #ccc;
    font-size: 24px;
    transition: color 0.3s ease, transform 0.3s ease;
}

.social-icons a:hover {
    color: #fff;
    transform: translateY(-3px);
}

.newsletter-form {
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.newsletter-form input {
    width: 100%;
    padding: 12px;
    border-radius: 5px;
    border: 1px solid #747474;
    background-color: #3e424f;
    color: #fff;
}

.newsletter-form button {
    padding: 12px;
    border-radius: 5px;
    border: none;
    background-color: #007bff;
    color: #fff;
    font-weight: bold;
    cursor: pointer;
    transition: background-color 0.3s ease;
}

.newsletter-form button:hover {
    background-color: #0056b3;
}


/* Barra inferior del footer */
.footer-bottom {
    text-align: center;
    padding-top: 20px;
    border-top: 1px solid #ffffff3a;
    font-size: 0.9em;
}

/* Responsive Design para el Footer */
@media (max-width: 768px) {
    .footer {
        padding: 40px 15px 15px 15px; /* Padding más reducido para evitar desbordamiento */
        margin: 0;
        width: 100%;
    }

    .footer-container {
        grid-template-columns: 1fr; /* Una sola columna en móviles */
        gap: 30px;
        margin-bottom: 30px;
        text-align: center; /* Centra el contenido */
    }

    .footer-column h3, .footer-column h4 {
        font-size: 1.4rem; /* Reduce el tamaño de los títulos */
        margin-bottom: 15px;
    }

    .footer-logo {
        font-size: 1.8rem; /* Reduce el tamaño del logo */
    }

    .footer-about p {
        font-size: 0.95rem; /* Texto más pequeño */
        line-height: 1.5;
        max-width: 350px;
        margin: 0 auto 20px auto; /* Centra el párrafo */
    }

    .social-icons {
        justify-content: center; /* Centra los iconos sociales */
        gap: 20px;
    }

    .social-icons a {
        font-size: 28px; /* Iconos más grandes para móvil */
    }

    .footer-links ul li {
        margin-bottom: 15px; /* Más espacio entre enlaces */
    }

    .footer-links a {
        font-size: 1rem;
    }

    .newsletter-form {
        flex-direction: column;
        max-width: 300px;
        margin: 0 auto; /* Centra el formulario */
    }

    .newsletter-form input {
        padding: 15px; /* Botones más grandes para móvil */
        font-size: 1rem;
    }

    .newsletter-form button {
        padding: 15px;
        font-size: 1rem;
        margin-top: 5px;
    }

    .footer-bottom {
        font-size: 0.85rem;
        padding-top: 15px;
    }
}

@media (max-width: 480px) {
    .footer {
        padding: 20px 10px 10px 10px; /* Padding mínimo para pantallas muy pequeñas */
        margin: 0;
        width: 100%;
    }

    .footer-container {
        gap: 25px;
    }

    .footer-logo {
        font-size: 1.6rem;
    }

    .footer-column h3, .footer-column h4 {
        font-size: 1.2rem;
    }

    .footer-about p {
        font-size: 0.9rem;
        max-width: 280px;
    }

    .social-icons {
        gap: 15px;
    }

    .social-icons a {
        font-size: 24px;
    }

    .newsletter-form {
        max-width: 280px;
    }

    .footer-bottom {
        font-size: 0.8rem;
    }
}

@media (min-width: 769px) {
    .newsletter-form {
        flex-direction: row;    
    }
}

src/app/componets/principal/Header.css.content:
.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px 40px;
    background-color: #273469;
    width: 100%;
    box-sizing: border-box;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 1000;
    transition: background-color 0.3s ease, box-shadow 0.3s ease, transform 0.3s ease, opacity 0.3s ease;
    transform: translateY(0);
    opacity: 1;
}

.header.hidden {
    transform: translateY(-100%);
    opacity: 0;
}


.header-left, .header-right {
    display: flex;
    align-items: center;
    gap: 20px;
}

.logo {
    text-decoration: none;
    font-family: var(--font-monserrat);
    font-size: 2.3lh;
    font-weight: 800;
    color: #fafaff;
}

.nav ul {
    text-decoration: none;
    list-style: none;
    display: flex;
    gap: 40px;
}

.nav li a {
    text-decoration: none;
    font-family: var(--font-lato);
    font-size: 1.1lh;
    font-weight: 400;
    color: #fafaff;
}

.nav li {
    transition: transform 0.3s ease;
}

.nav li:hover {
    transform: translateY(-3px);
}


.header-right .icon-button {
    background: none;
    border: none;
    padding: 0;
    cursor: pointer;
    font: inherit;
}

.header-right .icon-button,
.header-right .icon-link {
    color: #fafaff;
    transition: transform 0.3s ease;
    display: flex;
    align-items: center;
    padding: 8px;
    border-radius: 8px;
}

.desktop-icons {
    display: flex;
    align-items: center;
    gap: 8px;
}

.shop-icons-container {
    display: flex;
    align-items: center;
    gap: 8px;
}

.header-right .icon-button:hover,
.header-right .icon-link:hover {
    transform: translateY(-3px);
}

/* Estilos para el botón del menú móvil */
.mobile-menu-button {
    background: none;
    border: none;
    color: #fafaff;
    cursor: pointer;
    padding: 8px;
    display: none; /* Oculto por defecto, se muestra en móvil */
    align-items: center;
    justify-content: center;
    transition: transform 0.3s ease;
}

.mobile-menu-button:hover {
    transform: translateY(-3px);
}

/* Menú móvil deslizable */
.mobile-menu {
    position: fixed;
    top: 0;
    right: -100%; /* Oculto fuera de la pantalla */
    width: 280px;
    height: 100vh;
    background-color: #273469;
    z-index: 1000;
    transition: right 0.3s ease-in-out;
    padding-top: 80px; /* Espacio para el header */
    box-shadow: -2px 0 10px rgba(0, 0, 0, 0.1);
}

/* Botón X para cerrar el menú móvil */
.mobile-menu-close {
    position: absolute;
    top: 20px;
    right: 20px;
    background: none;
    border: none;
    color: #fafaff;
    cursor: pointer;
    padding: 8px;
    border-radius: 50%;
    transition: background-color 0.3s ease, transform 0.3s ease;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 40px;
    height: 40px;
}

.mobile-menu-close:hover {
    background-color: rgba(250, 250, 255, 0.1);
    transform: scale(1.1);
}

.mobile-menu-open {
    right: 0; /* Desliza hacia adentro */
}

.mobile-nav ul {
    list-style: none;
    padding: 0;
    margin: 0;
    display: flex;
    flex-direction: column;
}

.mobile-nav ul li {
    border-bottom: 1px solid rgba(250, 250, 255, 0.1);
}

.mobile-nav ul li a {
    display: block;
    padding: 20px 30px;
    color: #fafaff;
    text-decoration: none;
    font-family: var(--font-lato);
    font-size: 1.1rem;
    font-weight: 400;
    transition: background-color 0.3s ease, padding-left 0.3s ease;
}

.mobile-nav ul li a:hover {
    background-color: rgba(250, 250, 255, 0.1);
    padding-left: 40px;
}

/* Iconos de la tienda en móvil */
.mobile-shop-icons {
    padding: 30px;
    border-top: 1px solid rgba(250, 250, 255, 0.1);
    border-bottom: 1px solid rgba(250, 250, 255, 0.1);
}

.mobile-shop-icons .icon-button {
    display: flex;
    align-items: center;
    gap: 15px;
    width: 100%;
    padding: 15px 0;
    background: none;
    border: none;
    color: #fafaff;
    cursor: pointer;
    font-family: var(--font-lato);
    font-size: 1rem;
    transition: color 0.3s ease;
}

.mobile-shop-icons .icon-button:hover {
    color: #ccc;
}

/* Usuario en móvil */
.mobile-user {
    padding: 20px 30px;
}

.mobile-user .icon-link {
    display: flex;
    align-items: center;
    gap: 15px;
    color: #fafaff;
    text-decoration: none;
    font-family: var(--font-lato);
    font-size: 1.1rem;
    transition: color 0.3s ease;
}

.mobile-user .icon-link:hover {
    color: #ccc;
}

/* Responsive Design */
@media (max-width: 768px) {
    .header {
        padding: 10px 20px; /* Reduce el padding lateral */
    }

    /* Ocultar navegación de escritorio */
    .desktop-nav {
        display: none;
    }

    /* Ocultar iconos de escritorio */
    .desktop-icons {
        display: none;
    }

    /* Mostrar botón del menú móvil */
    .mobile-menu-button {
        display: flex;
    }

    /* Ajustar logo */
    .logo {
        font-size: 1.8rem; /* Tamaño más pequeño en móvil */
    }
}

@media (max-width: 480px) {
    .header {
        padding: 10px 15px; /* Aún menos padding en pantallas muy pequeñas */
    }

    .logo {
        font-size: 1.6rem;
    }

    .mobile-menu {
        width: 100%; /* Menú ocupa toda la pantalla en móviles muy pequeños */
    }
}

