        padding: 15px; /* Botones más grandes para móvil */
        font-size: 1rem;
    }

    .newsletter-form button {
        padding: 15px;
        font-size: 1rem;
        margin-top: 5px;
    }

    .footer-bottom {
        font-size: 0.85rem;
        padding-top: 15px;
    }
}

@media (max-width: 480px) {
    .footer {
        padding: 20px 10px 10px 10px; /* Padding mínimo para pantallas muy pequeñas */
        margin: 0;
        width: 100%;
    }

    .footer-container {
        gap: 25px;
    }

    .footer-logo {
        font-size: 1.6rem;
    }

    .footer-column h3, .footer-column h4 {
        font-size: 1.2rem;
    }

    .footer-about p {
        font-size: 0.9rem;
        max-width: 280px;
    }

    .social-icons {
        gap: 15px;
    }

    .social-icons a {
        font-size: 24px;
    }

    .newsletter-form {
        max-width: 280px;
    }

    .footer-bottom {
        font-size: 0.8rem;
    }
}

@media (min-width: 769px) {
    .newsletter-form {
        flex-direction: row;    
    }
}

src/components/layout/Header.css.content:
.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px 40px;
    background-color: var(--color-dark-blue);
    width: 100%;
    box-sizing: border-box;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 1000;
    transition: background-color 0.3s ease, box-shadow 0.3s ease, transform 0.3s ease, opacity 0.3s ease;
    transform: translateY(0);
    opacity: 1;
}

.header.hidden {
    transform: translateY(-100%);
    opacity: 0;
}


.header-left, .header-right {
    display: flex;
    align-items: center;
    gap: 20px;
}

.logo {
    text-decoration: none;
    font-family: var(--font-monserrat);
    font-size: 2.3lh;
    font-weight: 800;
    color: var(--color-light);
}

.nav ul {
    text-decoration: none;
    list-style: none;
    display: flex;
    gap: 40px;
}

.nav li a {
    text-decoration: none;
    font-family: var(--font-lato);
    font-size: 1.1lh;
    font-weight: 400;
    color: var(--color-light);
    position: relative;
    padding-bottom: 8px;
    transition: color 0.3s ease;
}

.nav li a::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 2px;
    background-color: var(--color-light);
    transition: width 0.3s ease;
}

.nav li a:hover::after,
.nav li a.active::after {
    width: 100%;
}

/* Estilos para menús desplegables */
.dropdown-item {
    position: relative;
}

.dropdown-item .nav li a,
.dropdown-item > a {
    display: flex;
    align-items: center;
    gap: 5px;
}

.dropdown-icon {
    transition: transform 0.3s ease;
}

.dropdown-item:hover .dropdown-icon {
    transform: rotate(180deg);
}

.dropdown-menu {
    position: absolute;
    top: calc(100% + 15px);
    left: 0;
    background-color: var(--color-light);
    border-radius: 8px;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
    padding: 10px 0;
    min-width: 180px;
    z-index: 1001;
    opacity: 1;
    transform: translateY(0);
    transition: all 0.3s ease;
    border: 1px solid rgba(48, 52, 63, 0.1);
}

/* Crear puente invisible para mantener el hover activo */
.dropdown-menu::before {
    content: '';
    position: absolute;
    top: -15px;
    left: 0;
    right: 0;
    height: 15px;
    background: transparent;
    z-index: 1000;
}

.dropdown-menu a {
    display: block !important;
    padding: 12px 20px !important;
    color: #30343f !important;
    text-decoration: none !important;
    font-family: var(--font-lato) !important;
    font-size: 0.95rem !important;
    font-weight: 400 !important;
    transition: background-color 0.3s ease, padding-left 0.3s ease !important;
    border: none !important;
    position: relative !important;
}

.dropdown-menu a:hover {
    background-color: rgba(48, 52, 63, 0.1) !important;
    padding-left: 25px !important;
    color: #30343f !important;
}

.dropdown-menu a::after {
    display: none !important;
}



.header-right .icon-button {
    background: none;
    border: none;
    padding: 0;
    cursor: pointer;
    font: inherit;
}

.header-right .icon-button,
.header-right .icon-link {
    color: var(--color-light);
    transition: transform 0.3s ease;
    display: flex;
    align-items: center;
    padding: 8px;
    border-radius: 8px;
    cursor: pointer;
}

.desktop-icons {
    display: flex;
    align-items: center;
    gap: 8px;
}

.shop-icons-container {
    display: flex;
    align-items: center;
    gap: 8px;
}

/* Hover unificado para todos los iconos del header */
.header-right .icon-button:hover,
.header-right .icon-link:hover,
.header-right .desktop-icons .shop-icons-container .icon-button:hover {
    transform: translateY(-3px) !important;
}

/* Estilos para el botón del menú móvil */
.mobile-menu-button {
    background: none;
    border: none;
    color: var(--color-light);
    cursor: pointer;
    padding: 8px;
    display: none; /* Oculto por defecto, se muestra en móvil */
    align-items: center;
    justify-content: center;
    transition: transform 0.3s ease;
}

.mobile-menu-button:hover {
    transform: translateY(-3px);
}

/* Menú móvil deslizable */
.mobile-menu {
    position: fixed;
    top: 0;
    right: -100%; /* Oculto fuera de la pantalla */
    width: 280px;
    height: 100vh;
    background-color: var(--color-dark-blue);
    z-index: 1000;
    transition: right 0.3s ease-in-out;
    padding-top: 80px; /* Espacio para el header */
    box-shadow: -2px 0 10px rgba(0, 0, 0, 0.1);
}

/* Botón X para cerrar el menú móvil */
.mobile-menu-close {
    position: absolute;
    top: 20px;
    right: 20px;
    background: none;
    border: none;
    color: var(--color-light);
    cursor: pointer;
    padding: 8px;
    border-radius: 50%;
    transition: background-color 0.3s ease, transform 0.3s ease;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 40px;
    height: 40px;
}

.mobile-menu-close:hover {
    background-color: rgba(250, 250, 255, 0.1);
    transform: scale(1.1);
}

.mobile-menu-open {
    right: 0; /* Desliza hacia adentro */
}

.mobile-nav ul {
    list-style: none;
    padding: 0;
    margin: 0;
    display: flex;
    flex-direction: column;
}

.mobile-nav ul li {
    border-bottom: 1px solid rgba(250, 250, 255, 0.1);
}

.mobile-nav ul li a {
    display: block;
    padding: 20px 30px;
    color: var(--color-light);
    text-decoration: none;
    font-family: var(--font-lato);
    font-size: 1.1rem;
    font-weight: 400;
    transition: background-color 0.3s ease, padding-left 0.3s ease;
    position: relative;
}

.mobile-nav ul li a:hover {
    background-color: rgba(250, 250, 255, 0.1);
    padding-left: 40px;
}

.mobile-nav ul li a.active {
    background-color: rgba(250, 250, 255, 0.15);
    font-weight: 600;
}

.mobile-nav ul li a.active::after {
    content: '';
    position: absolute;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
    width: 4px;
    height: 60%;
    background-color: var(--color-light);
}

/* Submenús móviles */
.mobile-submenu {
    background-color: rgba(0, 0, 0, 0.15);
    border-top: 1px solid rgba(250, 250, 255, 0.05);
}

.mobile-submenu a {
    display: block;
    padding: 15px 30px 15px 50px; /* Más indentación para mostrar jerarquía */
    color: var(--color-lavender);
    text-decoration: none;
    font-family: var(--font-lato);
    font-size: 1rem;
    font-weight: 300;
    transition: background-color 0.3s ease, padding-left 0.3s ease;
    border-bottom: 1px solid rgba(250, 250, 255, 0.05);
}

.mobile-submenu a:hover {
    background-color: rgba(250, 250, 255, 0.08);
    padding-left: 55px;
}

.mobile-submenu a:last-child {
    border-bottom: none;
}

/* Iconos de la tienda en móvil */
.mobile-shop-icons {
    padding: 30px;
    border-top: 1px solid rgba(250, 250, 255, 0.1);
    border-bottom: 1px solid rgba(250, 250, 255, 0.1);
}

.mobile-shop-icons .icon-button {
    display: flex;
    align-items: center;
    gap: 15px;
    width: 100%;
    padding: 15px 0;
    background: none;
    border: none;
    color: var(--color-light);
    cursor: pointer;
    font-family: var(--font-lato);
    font-size: 1rem;
    transition: color 0.3s ease;
}

.mobile-shop-icons .icon-button:hover {
    color: #ccc;
}

/* Usuario en móvil */
.mobile-user {
    padding: 20px 30px;
}

.mobile-user .icon-link {
    display: flex;
    align-items: center;
    gap: 15px;
    color: var(--color-light);
    text-decoration: none;
    font-family: var(--font-lato);
    font-size: 1.1rem;
    transition: color 0.3s ease;
}

.mobile-user .icon-link:hover {
    color: #ccc;
}

/* Responsive Design */
@media (max-width: 768px) {
    .header {
        padding: 10px 20px; /* Reduce el padding lateral */
    }

    /* Ocultar navegación de escritorio */
    .desktop-nav {
        display: none;
    }

    /* Ocultar iconos de escritorio */
    .desktop-icons {
        display: none;
    }

    /* Mostrar botón del menú móvil */
    .mobile-menu-button {
        display: flex;
    }

    /* Ajustar logo */
    .logo {
        font-size: 1.8rem; /* Tamaño más pequeño en móvil */
    }
}

@media (max-width: 480px) {
    .header {
        padding: 10px 15px; /* Aún menos padding en pantallas muy pequeñas */
    }

    .logo {
        font-size: 1.6rem;
    }

    .mobile-menu {
        width: 100%; /* Menú ocupa toda la pantalla en móviles muy pequeños */
    }
}

src/components/layout/Header.js.content:
"use client";
import React, { useState, useRef } from 'react';
import Link from 'next/link';
import { usePathname } from 'next/navigation'; // 1. Importar el hook
import './Header.css';
import { User, ShoppingCart, Search, Bookmark, Menu, X, ChevronDown } from 'lucide-react';
import { gsap } from "gsap"; // Importa gsap
import { useGSAP } from "@gsap/react"; // Importa el hook useGSAP

export default function Header() {
    // ocultar y mostrar los botones de la tienda
    const pathname = usePathname();
    const showShopIcons = pathname === '/shop';

    // Estado para el menú móvil
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

    // Estados para los menús desplegables
    const [activeDropdown, setActiveDropdown] = useState(null);

    // Referencia para los iconos del shop
    const shopIconsRef = useRef(null);

    // Estados para el scroll
    const [isHeaderVisible, setIsHeaderVisible] = useState(true);
    const lastScrollY = useRef(0);

    // Animación GSAP para los iconos del shop
    useGSAP(() => {
        if (showShopIcons && shopIconsRef.current) {
            const icons = shopIconsRef.current.children;

            // Configurar estado inicial de los iconos (desde la derecha)
            gsap.set(icons, {
                opacity: 0,
                x: 50
            });

            // Animar la entrada de los iconos deslizándose desde la derecha
            gsap.to(icons, {
                opacity: 1,
                x: 0,
                duration: 0.2,
                ease: "power1.out",
                stagger: 0.05
            });
        }
    }, [showShopIcons]);

    // Efecto para detectar el scroll con dirección
    React.useEffect(() => {
        const handleScroll = () => {
            const currentScrollY = window.scrollY;

            if (currentScrollY > lastScrollY.current && currentScrollY > 200) {
                // Scrolling hacia abajo y ya ha scrolled suficiente - ocultar header
                setIsHeaderVisible(false);
            } else {
                // Scrolling hacia arriba o cerca del top - mostrar header
                setIsHeaderVisible(true);
            }

            lastScrollY.current = currentScrollY;
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const handleSearchClick = () => alert('Mostrar búsqueda');
    const handleCartClick = () => alert('Mostrar carrito');
    const handleBookmarkClick = () => alert('Mostrar guardados');

    // Función para alternar el menú móvil
    const toggleMobileMenu = () => {
        setIsMobileMenuOpen(!isMobileMenuOpen);
    };

    // Función para cerrar el menú móvil al hacer clic en un enlace
    const closeMobileMenu = () => {
        setIsMobileMenuOpen(false);
    };

    // Funciones para manejar los menús desplegables
    const handleMouseEnter = (menu) => {
        setActiveDropdown(menu);
    };

    const handleMouseLeave = () => {
        setActiveDropdown(null);
    };

    return (
        <header className={`header ${!isHeaderVisible ? 'hidden' : ''}`}>
            <div className="header-left">
                <Link href="/" className="logo">
                    <span>Onovatech</span>
                </Link>

                {/* Navegación para escritorio */}
                <nav className="nav desktop-nav">
                    <ul>
                        <li 
                            className="dropdown-item" 
                            onMouseEnter={() => handleMouseEnter('tienda')}
                            onMouseLeave={handleMouseLeave}
                        >
                            <Link href="/shop" className={pathname === '/shop' ? 'active' : ''}>
                                Tienda <ChevronDown size={16} className="dropdown-icon" />
                            </Link>
                            {activeDropdown === 'tienda' && (
                                <div className="dropdown-menu">
                                    <Link href="/shop/categories" onClick={() => setActiveDropdown(null)}>Categorías</Link>
                                    <Link href="/shop/ofertas" onClick={() => setActiveDropdown(null)}>Ofertas</Link>
                                    <Link href="/shop/noticias" onClick={() => setActiveDropdown(null)}>Noticias</Link>
                                </div>
                            )}
                        </li>
                        <li 
                            className="dropdown-item" 
                            onMouseEnter={() => handleMouseEnter('comunidad')}
                            onMouseLeave={handleMouseLeave}
                        >
                            <Link href="/comunidad" className={pathname === '/comunidad' ? 'active' : ''}>
                                Comunidad <ChevronDown size={16} className="dropdown-icon" />
                            </Link>
                            {activeDropdown === 'comunidad' && (
                                <div className="dropdown-menu">
                                    <Link href="/comunidad/tutoriales" onClick={() => setActiveDropdown(null)}>Tutoriales</Link>
                                    <Link href="/comunidad/foros" onClick={() => setActiveDropdown(null)}>Foros</Link>
                                    <Link href="/comunidad/noticias" onClick={() => setActiveDropdown(null)}>Noticias</Link>
                                </div>
                            )}
                        </li>
                        <li><Link href="/servicios" className={pathname === '/servicios' ? 'active' : ''}>Servicios</Link></li>
                        <li><Link href="/contacto" className={pathname === '/contacto' ? 'active' : ''}>Contacto</Link></li>
                    </ul>
                </nav>
            </div>

            <div className='header-right'>
                {/* Iconos de la tienda para escritorio */}
                <div className="desktop-icons">
                    {showShopIcons && (
                        <div ref={shopIconsRef} className="shop-icons-container">
                            <button className="icon-button" onClick={handleSearchClick}>
                                <Search size={25}/>
                            </button>
                            <button className="icon-button" onClick={handleCartClick}>
                                <ShoppingCart size={25}/>
                            </button>
                            <button className="icon-button" onClick={handleBookmarkClick}>
                                <Bookmark size={25}/>
                            </button>
                        </div>
                    )}

                    <Link href="/login" className="icon-link">
                        <User size={25}/>
                    </Link>
                </div>

                {/* Botón hamburguesa para móvil */}
                <button className="mobile-menu-button" onClick={toggleMobileMenu}>
                    {isMobileMenuOpen ? <X size={25}/> : <Menu size={25}/>}
                </button>
            </div>

            {/* Menú móvil deslizable */}
            <div className={`mobile-menu ${isMobileMenuOpen ? 'mobile-menu-open' : ''}`}>
                {/* Botón X para cerrar el menú */}
                <button className="mobile-menu-close" onClick={closeMobileMenu}>
                    <X size={24}/>
                </button>

                <nav className="mobile-nav">
                    <ul>
                        <li>
                            <Link href="/shop" onClick={closeMobileMenu} className={pathname === '/shop' ? 'active' : ''}>Tienda</Link>
                            <div className="mobile-submenu">
                                <Link href="/shop/categories" onClick={closeMobileMenu}>Categorías</Link>
                                <Link href="/shop/ofertas" onClick={closeMobileMenu}>Ofertas</Link>
                                <Link href="/shop/noticias" onClick={closeMobileMenu}>Noticias</Link>
                            </div>
                        </li>
                        <li>
                            <Link href="/comunidad" onClick={closeMobileMenu} className={pathname === '/comunidad' ? 'active' : ''}>Comunidad</Link>
                            <div className="mobile-submenu">
                                <Link href="/comunidad/tutoriales" onClick={closeMobileMenu}>Tutoriales</Link>
                                <Link href="/comunidad/foros" onClick={closeMobileMenu}>Foros</Link>
                                <Link href="/comunidad/noticias" onClick={closeMobileMenu}>Noticias</Link>
                            </div>
                        </li>
                        <li><Link href="/servicios" onClick={closeMobileMenu} className={pathname === '/servicios' ? 'active' : ''}>Servicios</Link></li>
                        <li><Link href="/contacto" onClick={closeMobileMenu} className={pathname === '/contacto' ? 'active' : ''}>Contacto</Link></li>
                    </ul>

                    {/* Iconos de la tienda para móvil */}
                    {showShopIcons && (
                        <div className="mobile-shop-icons">
                            <button className="icon-button" onClick={handleSearchClick}>
                                <Search size={25}/> <span>Buscar</span>
                            </button>
                            <button className="icon-button" onClick={handleCartClick}>
                                <ShoppingCart size={25}/> <span>Carrito</span>
                            </button>
                            <button className="icon-button" onClick={handleBookmarkClick}>
                                <Bookmark size={25}/> <span>Guardados</span>
                            </button>
                        </div>
                    )}

                    <div className="mobile-user">
                        <Link href="/login" className="icon-link" onClick={closeMobileMenu}>
                            <User size={25}/> <span>Iniciar Sesión</span>
                        </Link>
                    </div>
                </nav>
            </div>
        </header>
    );
}

src/components/lib/strapi.js.content:
export async function queryAPI(path) {
    const strapiHost = process.env.NEXT_PUBLIC_STRAPI_HOST;
    const strapiToken = process.env.NEXT_PUBLIC_STRAPI_API_TOKEN;

    if (!strapiHost) {
      console.error("Error: El host de Strapi no está configurado.");
      return null;
    }
    if (!strapiToken) {
      console.error("Error: El token de la API de Strapi no está configurado.");
      return null;
    }

    const url = new URL(path, strapiHost);

    try {
      const response = await fetch(url.href, {
        headers: {
          'Authorization': `Bearer ${strapiToken}`,
        },
      });

      if (!response.ok) {
        console.error(`Error al hacer la petición: ${response.statusText}`);
        return null;
      }

      const data = await response.json();
      return data;

    } catch (error) {
      console.error("Error de conexión con la API de Strapi:", error);
      return null;
    }
  }

src/components/product/ProductCard.css.content:
/* ProductCard Component Styles */
.product-card {
  background: var(--color-light);
  border-radius: 20px;
  border: 3px solid #d0d0d0;
  overflow: hidden;
  transition: all 0.3s ease;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
  cursor: pointer;
  width: 100%;
  max-width: 280px;
  margin: 0 auto;
}

.product-card:hover {
  border-color: var(--color-dark-blue);
  transform: translateY(-3px);
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.2);
}

/* Sección de imagen del producto */
.product-image-container {
  position: relative;
  width: 100%;
  aspect-ratio: 1 / 1;
  overflow: hidden;
  border-radius: 20px 20px 0 0;
}

.product-image {
  width: 100%;
  height: 100%;
  position: relative;
  overflow: hidden;
}

.product-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.3s ease;
}

.product-card:hover .product-image img {
  transform: scale(1.05);
}

/* Botón de ir a detalles */
.details-link {
  position: absolute;
  top: 12px;
  right: 12px;
  width: 40px;
  height: 40px;
  background: #d0d0d0;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--color-dark-gray);
  text-decoration: none;
  opacity: 1;
  transition: all 0.3s ease;
  z-index: 3;
}

.product-card:hover .details-link {
  transform: translateY(-3px);
}

.details-link:hover {
  background: var(--color-dark-blue);
  color: white;
  transform: translateY(-3px);
}

/* Sección de acciones */
.product-actions {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: var(--color-dark-blue);
  display: flex;
  justify-content: space-around;
  padding: 12px;
  transform: translateY(100%);
  transition: transform 0.3s ease;
  z-index: 2;
  pointer-events: auto;
}

/* Área específica para activar hover */
.hover-trigger-area {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 40px;
  z-index: 1;
  pointer-events: auto;
}

.product-actions.visible {
  transform: translateY(0);
}

.action-btn {
  background: transparent;
  border: none;
  color: white;
  width: 44px;
  height: 44px;
  border-radius: 50%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  margin: 0 2px;
}

.action-btn:hover {
  background: rgba(255, 255, 255, 0.2);
  transform: translateY(-2px);
}

.cart-btn:hover {
  background: var(--color-lavender);
  color: var(--color-dark-blue);
}

.favorite-btn.active {
  background: #ff4757;
  color: white;
}

.favorite-btn:hover {
  background: #ff6b7d;
  color: white;
}

/* Indicadores de imágenes */
.image-indicators {
  display: flex;
  justify-content: center;
  gap: 4px;
  padding: 8px 0;
  margin-top: 4px;
}

.indicator {
  width: 20px;
  height: 3px;
  border-radius: 2px;
  background: #909091;
  cursor: pointer;
  transition: all 0.3s ease;
}

.indicator.active {
  background: var(--color-dark-blue);
}

.indicator:hover {
  background: var(--color-dark-blue);
}

/* Sección de detalles */
.product-details {
  padding: 16px;
  background: var(--color-light);
  border-radius: 0 0 20px 20px;
  text-align: center;
}

